def cipher(text, shift, encrypt=True):    """    encrypt and decrypt a text, each letter is replaced     by a letter some fixed number of positions down the alphabet.    Parameters    ----------    text : text        text you wish to encrypt or decrypt        shift : integer        shift the original text by this integer of positions down the alphabet.         Returns    -------      A new text    Examples    --------    >>> from cipher_hz2697 import cipher_hz2697    example of encrypt    >>> text = 'love'    >>> shift = 1    >>> cipher_hz2697.cipher(text, shift, encrypt = True)    'mpwf'    example of decrypt    >>> text = 'mpwf'    >>> shift = 1    >>> cipher_hz2697.cipher(text, shift, encrypt = False)    'love'    """    alphabet = 'abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ'    new_text = ''    for c in text:        index = alphabet.find(c)        if index == -1:            new_text += c        else:            new_index = index + shift if encrypt == True else index - shift            new_index %= len(alphabet)            new_text += alphabet[new_index:new_index+1]    return new_text